import { defaultHelpLink } from "./common";
import { DotnetChecker } from "./dotnetChecker";
import { BackendExtensionsInstallError } from "./errors";
import { cpUtils } from "./cpUtils";
import { IDepsLogger } from "./checker";

// NOTE: change these constants if function plugin scaffold changes
const defaultOutputPath = "bin";
const defaultCsprojPath = "extensions.csproj";

export class BackendExtensionsInstaller {
  private readonly _dotnetChecker: DotnetChecker;
  private readonly _logger: IDepsLogger;

  constructor(dotnetChecker: DotnetChecker, logger: IDepsLogger) {
    this._dotnetChecker = dotnetChecker;
    this._logger = logger;
  }

  public async install(backendRoot: string, csprojPath: string = defaultCsprojPath, outputPath: string = defaultOutputPath): Promise<void> {
    if (!outputPath) {
      outputPath = defaultOutputPath;
    }

    const dotnetExecPath = await this._dotnetChecker.getDotnetExecPath();

    if (dotnetExecPath === "") {
      await this._logger.error(`Failed to run backend extension install, .NET SDK executable not found`);
      throw new BackendExtensionsInstallError("Failed to run backend extension install, .NET SDK executable not found", defaultHelpLink);
    }

    try {
      await cpUtils.executeCommand(
        backendRoot,
        this._logger,
        { shell: false },
        dotnetExecPath,
        "build",
        csprojPath,
        "-o",
        outputPath,
        "--ignore-failed-sources"
      );
    } catch (error) {
      await this._logger.error(`Failed to run backend extension install: error = '${error}'`);
      throw new BackendExtensionsInstallError(`Failed to run backend extension install: error = '${error}'`, defaultHelpLink);
    }
  }
}
